mapper.select:
select a.*, b.custName vatSellerName, c.custName vatBuyerName, sum(d.dataAmt) vatSum from swc_vat a left join swc_cust b on a.vatSellerCustNo = b.custNo left join swc_cust c on a.vatBuyerCustNo = c.custNo left join swc_soppdata01 d on a.vatSerial = d.vatSerial and d.attrib not like 'XXX%' where a.compNo = #{compNo} and a.attrib not like 'XXX%'
and a.vatSellerCustNo = #{vatSellerCustNo}
and a.vatType = #{vatType}
and a.vatIssueDate BETWEEN #{vatIssueDateFrom} and #{vatIssueDateTo}
and a.vatSerial like CONCAT('%', #{vatSerial}, '%')
and a.vatRemark like CONCAT('%', #{vatRemark}, '%')
and a.vatIssueDate Between #{listDateFrom} and now()
group by a.vatSerial order by a.vatIssueDate desc

select a.*, b.custName vatSellerName, c.custName vatBuyerName, sum(case when a.vatType='B' then a.vatAmount end) AS amount, sum(case when a.vatType='B' then a.vatTax end) AS tax from swc_vat a left join swc_cust b on a.vatSellerCustNo = b.custNo left join swc_cust c on a.vatBuyerCustNo = c.custNo left join swc_soppdata01 d on a.vatSerial = d.vatSerial and d.attrib not like 'XXX%' where a.compNo = #{compNo} and a.attrib not like 'XXX%' and a.vatType = 'B'
and a.vatSellerCustNo = #{vatSellerCustNo}
and a.vatType = #{vatType}
and a.vatIssueDate BETWEEN #{vatIssueDateFrom} and #{vatIssueDateTo}
and a.vatSerial like CONCAT('%', #{vatSerial}, '%')
and a.vatRemark like CONCAT('%', #{vatRemark}, '%')
and a.vatIssueDate Between #{listDateFrom} and now()
group by a.vatSellerCustNo order by a.vatIssueDate desc

select a.*, b.custName vatSellerName, c.custName vatBuyerName, sum(case when a.vatType='S' then a.vatAmount end) AS amount, sum(case when a.vatType='S' then a.vatTax end) AS tax from swc_vat a left join swc_cust b on a.vatSellerCustNo = b.custNo left join swc_cust c on a.vatBuyerCustNo = c.custNo left join swc_soppdata01 d on a.vatSerial = d.vatSerial and d.attrib not like 'XXX%' where a.compNo = #{compNo} and a.attrib not like 'XXX%' and a.vatType = 'S'
and a.vatSellerCustNo = #{vatSellerCustNo}
and a.vatType = #{vatType}
and a.vatIssueDate BETWEEN #{vatIssueDateFrom} and #{vatIssueDateTo}
and a.vatSerial like CONCAT('%', #{vatSerial}, '%')
and a.vatRemark like CONCAT('%', #{vatRemark}, '%')
and a.vatIssueDate Between #{listDateFrom} and now()
group by a.vatBuyerCustNo order by a.vatIssueDate desc

	select a.*, b.desc03 bankCodeN from swc_card a
   	left join swc_code b on b.code03 = a.cardBank
	where a.compNo = #{compNo} and a.attrib not like 'XXX%';
