mapper.select:
select subquery1.*
from
	(
	select a.*, b.userName, b.compNo
	from swc_notice a
	left join swc_user b on a.userNo = b.userNo
	where b.compNo = #{compNo}

		and a.userNo = #{userNo}


		and a.notiTitle like concat('%',#{notiTitle},'%')


		and a.notiContents like concat('%',#{notiContents},'%')


		and date_format(a.regDatetime, '%Y-%m-%d') >= #{regSDate}


		and date_format(a.regDatetime, '%Y-%m-%d') <= #{regEDate}

	) subquery1 where userName IS NOT NULL

where
	date_format(subquery1.regDatetime, '%Y-%m-%d') like concat('%',#{sSearch},'%')
	or subquery1.notiTitle like concat('%',#{sSearch},'%')
	or subquery1.notiContents like concat('%',#{sSearch},'%')



	order by ${orderColumn} desc


	order by ${orderColumn} asc


limit #{limit} offset #{offset}

select count(*) from ( select subquery1.* from ( select a.*, b.userName, b.compNo from swc_notice a left join swc_user b on a.userNo = b.userNo where b.compNo = #{compNo}
and a.userNo = #{userNo}
and a.notiTitle like concat('%',#{notiTitle},'%')
and a.notiContents like concat('%',#{notiContents},'%')
and date_format(a.regDatetime, '%Y-%m-%d') >= #{regSDate}
and date_format(a.regDatetime, '%Y-%m-%d') <= #{regEDate}
) subquery1 where userName IS NOT NULL
where date_format(subquery1.regDatetime, '%Y-%m-%d') like concat('%',#{sSearch},'%') or subquery1.notiTitle like concat('%',#{sSearch},'%') or subquery1.notiContents like concat('%',#{sSearch},'%')
order by ${orderColumn} desc
order by ${orderColumn} asc
) t

mapper.update:
update swc_notice 
set 
 notiTitle=#{notiTitle}
,notiContents=#{notiContents}
where notiNo=#{notiNo}
