mapper.select:
select a.schedNo, a.schedTitle, a.schedFrom, a.schedTo, a.schedPlace, a.schedDesc, b.custName, c.userName, d.desc03 schedTypeN, e.desc03 schedCatN from swc_sched a left join swc_cust b on a.custNo = b.custNo left join swc_user c on a.userNo = c.userNo left join swc_code d on a.schedType = d.codeNo left join swc_code e on a.schedCat = e.codeNo where a.attrib not like 'XXX%' and a.compNo = #{compNo}
UNION
select a.salesNo schedNo, a.salesTitle schedTitle, a.salesFrdatetime schedFrom, a.salesTodatetime schedTo, a.salesPlace schedPlace, a.salesDesc schedDesc, b.custName, c.userName, d.desc03 schedTypeN, e.desc03 schedCatN from swc_sales a left join swc_cust b on a.custNo = b.custNo left join swc_user c on a.userNo = c.userNo left join swc_code d on a.schedType = d.codeNo left join swc_code e on a.salesType = e.codeNo where a.attrib not like 'XXX%' and a.compNo = #{compNo}
UNION
select a.techdNo schedNo, a.techdTitle schedTitle, a.techdFrom schedFrom, a.techdTo schedTo, a.techdPlace schedPlace, a.techdDesc schedDesc, b.custName, c.userName, d.desc03 schedTypeN, e.desc03 schedCatN from swc_techd a left join swc_cust b on a.custNo = b.custNo left join swc_user c on a.userNo = c.userNo left join swc_code d on a.schedType = d.codeNo left join swc_code e on a.techdType = e.codeNo where a.attrib not like 'XXX%' and a.compNo = #{compNo}
order by schedFrom desc

select a.*, b.custName, c.soppTitle, d.userName, e.desc03 schedCatN, f.desc03 schedTypeN  
from swc_sched a 
left join swc_cust b on a.custNo=b.custNo
left join swc_sopp c on a.soppNo=c.soppNo
left join swc_user d on a.userNo=d.userNo
left join swc_code e on a.schedCat = e.codeNo
left join swc_code f on a.schedType = f.codeNo
where a.attrib not like 'XXX%' and a.compNo = #{compNo}

 	and a.userNo = #{userNo}


 	and a.soppNo = #{soppNo}


 	and a.custNo = #{custNo}


 	and a.schedCat = #{schedCat}


 	and date_format(a.schedFrom, '%Y-%m-%d') >= #{schedFrom}


	and date_format(a.schedTo, '%Y-%m-%d') <= #{schedTo}

order by schedNo

mapper.insert:
			SELECT userNo FROM swc_user where userId = #{userId} 

 insert into swc_sched (
	userNo
	,compNo
	,soppNo
	,custNo
	,schedName
	,schedFrom
	,schedTo
	,schedTitle
	,schedDesc
	,subschedNo
	,schedActive
	,schedAllday
	,remindflag
	,schedPlace
	,schedType
	,schedColor
	,schedCat
	,attrib )
 values (
	#{userNo}
	,#{compNo}
	,#{soppNo}
	,#{custNo}
	,#{schedName}
	,#{schedFrom}
	,#{schedTo}
	,#{schedTitle}
	,#{schedDesc}
	,#{subschedNo}
	,#{schedActive}
	,#{schedAllday}
	,#{remindflag}
	,#{schedPlace}
	,#{schedType}
	,#{schedColor}
	,#{schedCat}
	,'100000' )

mapper.update:
 update swc_sched set 
	 soppNo=#{soppNo}
	,custNo=#{custNo}
	,schedName=#{schedName}
	,schedFrom=#{schedFrom}
	,schedTo=#{schedTo}
	,schedTitle=#{schedTitle}
	,schedDesc=#{schedDesc}
	,subschedNo=#{subschedNo}
	,schedActive=#{schedActive}
	,schedAllday=#{schedAllday}
	,remindflag=#{remindflag}
	,schedType=#{schedType}
	,schedCat=#{schedCat}
	,schedPlace=#{schedPlace}
 where schedNo=#{schedNo}
